import React, { useEffect } from "react";
import { Route } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCollectionLoading } from "../../redux/shop/shopSelectors";
import CollectionOverview from "../../Components/collectionOverview/CollectionOverview";
import Collection from "../collection/Collection";
import {
  firestore,
  convertColectionSnapShotToMap
} from "../../firebase/firebase.utils";
import { fetchShopCollectionsCall } from "../../redux/shop/shopActions";
import Spinner from "../../Components/spinner/Spinner";

const CollectionsOverviewWithSpinner = Spinner(CollectionOverview);
const CollectionPageWithSpinner = Spinner(Collection);

const Shop = ({ match }) => {
  const { isLoading } = useSelector(
    createStructuredSelector({
      isLoading: selectCollectionLoading
    })
  );
  const dispatch = useDispatch();
  const updateCollection = () => dispatch(fetchShopCollectionsCall());

  useEffect(() => {
    updateCollection();
  }, []);

  return (
    <div className="shop-page">
      <Route
        exact
        path={`${match.path}`}
        render={props => (
          <CollectionsOverviewWithSpinner isLoading={isLoading} {...props} />
        )}
      />
      <Route
        path={`${match.path}/:collectionId`}
        render={props => (
          <CollectionPageWithSpinner isLoading={isLoading} {...props} />
        )}
      />
    </div>
  );
};

export default Shop;
