import {
  FETCH_COLLECTIONS_FAIL,
  FETCH_COLLECTIONS_PENDING,
  FETCH_COLLECTIONS_SUCCESS
} from "./shopActionTypes";

const initialState = {
  collections: null,
  isPending: false
};

const shopReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case UPDATE_COLLECTIONS:
      return {
        ...state,
        collections: payload
      };
    default:
      return state;
  }
};

export default shopReducer;
