import { takeEvery } from "redux-saga/effects";
import {
  firestore,
  convertCollectionSnapShotToMap
} from "../../firebase/firebase.utils";
import { fetchShopCollectionsSuccess } from "./shopActions";
import {
  FETCH_COLLECTIONS_FAIL,
  FETCH_COLLECTIONS_PENDING,
  FETCH_COLLECTIONS_SUCCESS
} from "./shopActionTypes";

export function* fetchCollectionsAsync() {
  yield console.log("fired");
  const collectionRef = firestore.collection("collections");
  const snapshot = yield;

  collectionRef
    .get()
    .then(snapshot => {
      const collectionsMap = convertCollectionSnapShotToMap(snapshot);
      dispatch(fetchShopCollectionsSuccess(collectionsMap));
    })
    .catch(err =>
      dispatch({
        type: FETCH_COLLECTIONS_FAIL,
        payload: err.message
      })
    );
}

export function* fetchCollectionsStart() {
  yield takeEvery(FETCH_COLLECTIONS_PENDING, fetchCollectionsAsync);
}
