import { takeLatest, put, all, call } from "redux-saga/effects";
import userActionsTypes from "./userActionTypes";
import {
  auth,
  googleProvider,
  createUserProfileDocument
} from "../../firebase/firebase.utils";
import {
  googleSignInSuccess,
  googleSignInFailed,
  emailSignInFailed,
  emailSignInSuccess
} from "./userActions";

export function* signWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapShot = yield userRef.get();
    yield put(googleSignInSuccess({ id: userSnapShot.id, ...user }));
  } catch (error) {
    yield put(googleSignInFailed(error));
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(userActionsTypes.GOOGLE_SIGN_IN_START, signWithGoogle);
}

export function* signWithEmail({ payload: { email, password } }) {
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapShot = yield userRef.get();
    yield put(emailSignInSuccess({ id: userSnapShot.id, ...user }));
  } catch (error) {
    yield put(emailSignInFailed(error));
  }
}

export function* onEmailSign() {
  yield takeLatest(userActionsTypes.EMAIL_SIGN_IN_START, signWithEmail);
}

export function* userSagas() {
  yield all([call(onGoogleSignInStart)]);
}
