import React, { useState } from "react";
import { auth, signInWithGoogle } from "../../firebase/firebase.utils";
import sign_in from "./sign-in.svg";
import profile_pic from "./profile.svg";
import "./customSignIn.css";

const CustomSignIn = () => {
  const [user, setUser] = useState({ email: "", password: "" });

  const handleSubmit = async event => {
    event.preventDefault();

    const { email, password } = user;

    try {
      await auth.signInWithEmailAndPassword(email, password);
      setUser({ email: "", password: "" });
    } catch (error) {
      console.log(error.message);
    }
  };

  const handleChange = event => {
    const { value, name } = event.target;
    setUser({ ...user, [name]: value });
  };
  return (
    <div className="container">
      <div className="img">
        <img src={sign_in} alt="loginImg" />
      </div>
      <div className="login-container">
        <form onSubmit={handleSubmit}>
          <img src={profile_pic} alt="avatar" className="avatar" />
          <h2>SIGN IN</h2>
          <div className="social-container">
            <span className="social">
              <i className="fab fa-facebook-f"></i>
            </span>
            <span className="social">
              <i className="fab fa-google-plus-g"></i>
            </span>
            <span className="social">
              <i className="fab fa-linkedin-in"></i>
            </span>
          </div>
          <span>or use your account</span>
          <div className="input-div one focus">
            <div className="icon">
              <i className="fas fa-user" />
            </div>
            <div>
              <h5>Email</h5>
              <input className="input" type="text"></input>
            </div>
          </div>
          <div className="input-div two focus">
            <div className="icon">
              <i className="fas fa-lock" />
            </div>
            <div>
              <h5>Password</h5>
              <input
                className="input"
                type="password"
                onChange={handleChange}
              ></input>
            </div>
          </div>
          <a href="#">Don't have account? Sign Up</a>
          <input type="submit" className="btn" value="Login"></input>
        </form>
      </div>
    </div>
  );
};

export default CustomSignIn;
