import {
  FETCH_COLLECTIONS_SUCCESS,
  FETCH_COLLECTIONS_PENDING,
  FETCH_COLLECTIONS_FAIL
} from "./shopActionTypes";
import {
  firestore,
  convertColectionSnapShotToMap
} from "../../firebase/firebase.utils";

const setLoading = () => ({
  type: FETCH_COLLECTIONS_PENDING
});

const fetchShopCollectionsSuccess = collectionsMap => ({
  type: FETCH_COLLECTIONS_SUCCESS,
  payload: collectionsMap
});

export const fetchShopCollectionsCall = () => {
  return dispatch => {
    const collectionRef = firestore.collection("collections");
    dispatch(setLoading());

    collectionRef
      .get()
      .then(snapshot => {
        const collectionsMap = convertColectionSnapShotToMap(snapshot);
        dispatch(fetchShopCollectionsSuccess(collectionsMap));
      })
      .catch(err =>
        dispatch({
          type: FETCH_COLLECTIONS_FAIL,
          payload: err.message
        })
      );
  };
};
