import React, { useEffect } from "react";
import { Route } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { createStructuredSelector } from "reselect";
import {
  selectCollectionLoading,
  selectIsCollectionLoaded
} from "../../redux/shop/shopSelectors";
import CollectionOverviewContainer from "../../Components/collectionOverview/CollectionOverviewContainer";
import Collection from "../collection/Collection";
import { fetchShopCollectionsCall } from "../../redux/shop/shopActions";
import Spinner from "../../Components/spinner/Spinner";

// const CollectionsOverviewWithSpinner = Spinner(CollectionOverview);
const CollectionPageWithSpinner = Spinner(Collection);

const Shop = ({ match }) => {
  const { isCollectionLoaded } = useSelector(
    createStructuredSelector({
      isLoading: selectCollectionLoading,
      isCollectionLoaded: selectIsCollectionLoaded
    })
  );
  const dispatch = useDispatch();
  const updateCollection = () => dispatch(fetchShopCollectionsCall());

  useEffect(() => {
    updateCollection();
    // eslint-disable-next-line
  }, []);

  return (
    <div className="shop-page">
      <Route
        exact
        path={`${match.path}`}
        component={CollectionOverviewContainer}
      />
      <Route
        path={`${match.path}/:collectionId`}
        render={props => (
          <CollectionPageWithSpinner
            isLoading={!isCollectionLoaded}
            {...props}
          />
        )}
      />
    </div>
  );
};

export default Shop;
