const SET_CURRENT_USER = "SET_CURRENT_USER";

const initialState = {
  currentUser: null
};

const userReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case SET_CURRENT_USER:
      return {
        ...state,
        currentUser: payload
      };

    default:
      break;
  }
};

export default userReducer;
